mapper.select:
SELECT ccr.title AS challengeName, cat.name AS categoryName, ccr.points AS point, ucr.finish_time FROM user_challenge_record ucr JOIN cyrangechallenges ccr ON ucr.challenge_id = ccr.question_id JOIN categories cat ON ccr.category = cat.id
AND ucr.user_id = #{userId}
ORDER BY ucr.finish_time DESC

mapper.update:
update user
username=#{username}, phone=#{phone}, mail=#{mail},
where id=#{id}
